import React, { useState, useEffect } from 'react';

const useLocalStorage = (key, tokenFromLogin, tokenFromSignUp) => {
  const [state, setState] = useState(() => {
    try {
      let value = JSON.parse(window.localStorage.getItem(key));
      return value;
    }
    catch(e) {
      console.log(e)
      console.log(window.localStorage.getItem(key));
      alert('Local storage realized an error!');
    }
  });
  useEffect(() => {
    if (state) {
      window.localStorage.setItem(key, state);
    }
    else if (tokenFromLogin) {
      window.localStorage.setItem(key, tokenFromLogin);
    }
    else if (tokenFromSignUp) {
      window.localStorage.setItem(key, tokenFromSignUp);
    }
    else {
      window.localStorage.setItem(key, null);
    }
  }, [state, tokenFromLogin, tokenFromSignUp]);
  return [state];
};

export default useLocalStorage;
