import { useState } from 'react';
import JoblyApi from '../api';

const useApplyForJob = async (user) => {
  const applicationIds = await JoblyApi.getUser(user.username)
  const [applications, setApplications] = useState(user ? user.applications : [])

  const applyForJob = async (job) => {
    const { username } = user;
    const { id } = job;

    let res = await JoblyApi.applyForJob(username, id);
    return res;
  };

  return [applyForJob, applications, setApplications];
};

export default useApplyForJob;
