import React, { useState, useEffect } from 'react';

const useLocalStorage = (key, tokenFromLogin, tokenFromSignUp) => {
  const [state, setState] = useState(() => {
    try {
      let value = JSON.parse(window.localStorage.getItem(key)) || tokenFromLogin || tokenFromSignUp;
      return value;
    }
    catch(e) {
      console.log(e)
      alert('Local storage realized an error!');
    }
  });
  useEffect(() => {
    window.localStorage.setItem(key, state || tokenFromLogin || tokenFromSignUp);
  }, [state, tokenFromLogin, tokenFromSignUp]);
  return [state];
};

export default useLocalStorage;
