import { useState, useEffect } from 'react';

const useGetUser = (tokenFromLogin, tokenFromSignUp, tokenFromLocalStorage, JoblyApi) => {
  const [user, setUser] = useState();
  const [applications, setApplications] = useState(user ? user.applications : [])

  useEffect(() => {
    const getInformation = async () => {
      if (!(tokenFromLocalStorage || tokenFromLogin || tokenFromSignUp)) {
        if (user) setUser(null);
        return;
      }
      else {
        const userResp = await JoblyApi.getUser(tokenFromLocalStorage || tokenFromSignUp || tokenFromLogin);
        setUser(userResp);
      }
    };
    getInformation();
  }, [tokenFromLogin, tokenFromSignUp, tokenFromLocalStorage, JoblyApi, applications]);

  return [user, setUser, JoblyApi, applications, setApplications];
};

export default useGetUser;
