import { useHistory } from 'react-router-dom';
import JoblyApi from '../api';

const useUserUpdate = ({ setUser, JoblyApi, tokenFromLocalStorage }) => {
  const history = useHistory();
  const handleUpdate = async (e, username, formData, setShowMessage) => {
    try {
      e.preventDefault();
      await JoblyApi.updateUser(username, formData);
      const res = await JoblyApi.getUser(tokenFromLocalStorage);
      setUser(res);
      history.push('/');
    }
    catch(err) {
      let errors = err.map(message => <p>{message}</p>);
      setShowMessage(errors);
    }
  };

  return handleUpdate;
};

export default useUserUpdate;
